import { createMuiTheme } from "@material-ui/core/styles";

const theme = createMuiTheme({
  typography: {
    fontFamily: [
      "-apple-system",
      "BlinkMacSystemFont",
      '"Segoe UI"',
      "Roboto",
      '"Helvetica Neue"',
      "Arial",
      "sans-serif",
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"'
    ].join(",")
  },
  header: {
    margin: 20,
    padding: 20,
    border: "1px solid #DDD"
  },
  overrides: {
    MuiAppBar: {
      margin: 20,
      padding: 20,
      border: "1px solid #DDD"
    }
  }
});

export default theme;
